export async function searchBus(keyword) {
  if (!keyword) return [];
  return Object.values(BUS_DATA).filter(b =>
    b.number.includes(keyword) || b.name.includes(keyword)
  );
}

export async function getBusStops(routeId) {
  const bus = BUS_DATA[routeId];
  if (!bus) return [];
  return bus.stops;
}